import React, { Component } from 'react'

export default class ShowProduct extends Component {
  constructor(props) {
    super(props)
    this.state= {
      myProduct: null,
      product: {},
      prod: 0
    }
  }
  //por que mierda no sube las props al state
  componentDidMount() {
    this.props.fromApp();
    if(this.props.user) {
      const productSelected= this.props.user.products.filter(product=> product._id==this.props.productId)[0];
      this.setState({
        ...this.state,
        product: productSelected,
        // myProduct: this.props.user
      })
    }


  }
  change() {
    this.setState({
      ...this.state,
      product:9,
      valueForB: undefined
    });
  }
  render() {

    return (
      <div>
        jnjkn
        <h1>{this.props.user.username}</h1>

      {/* <h1>{this.props.product.description}</h1>  */}
        <h1>{this.state.product._id}</h1>
        <button onClick={(e)=>this.change(e)}>change</button>
      </div>
    )
  }
}
