import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import { NavLink } from "react-router-dom";

import 'date-fns';
import DateFnsUtils from '@date-io/date-fns';
import TextField from '@material-ui/core/TextField';
import {
  MuiPickersUtilsProvider,
  KeyboardTimePicker,
  KeyboardDatePicker,
} from '@material-ui/pickers';

export default class CreateBid extends Component {
  constructor(props) {
    super();
    this.state = {
      deadLine:"",
      from : "",
      to:"",
      location:""
    }
  }
  updateFormData(e,field){
    let newState = {
      ...this.state
    };
    newState[field] = e.target.value;

    this.setState(newState);
  }

  sendState(e) {
    e.preventDefault();
    console.log(e)
    // this.props.fromApp(this.state);
    // return <Redirect to='/' />
  }

  render() {
    return (
      <form>

        <label  htmlFor="input-day">Día de finalización</label>
        <input type="date" placeholder="Cuando quieres que se lo lleven" name="dia" id="input-day" onChange={e => this.updateFormData(e, "name")} ></input>
        <label htmlFor="dia">Hora de recogida</label>
        <input type="time" placeholder="Tagline" name="hora"  onChange={e => this.updateFormData(e, "tagline")} ></input>
        <label htmlFor="dia">Finalización de recogida</label>
        <input type="time" placeholder="Created by" name="fin"  onChange={e => this.updateFormData(e, "contributed_by")}></input>
        <button  onClick={e => this.sendState(e)}>Submit</button>
        {/* <KeyboardTimePicker
          margin="normal"
          id="time-picker"
          label="Hora de recogida"
          value={this.state.selectedDate}
          onChange={this.state.handleDateChange}
          KeyboardButtonProps={{
            'aria-label': 'change time',
          }}
        /> */}
      </form>
    )
  }
}
