import React, { Component } from "react";


export default class ShowProduct extends Component {
  constructor(props) {
    super(props);
    this.state = {
      product: {}
    };
  }

  componentDidMount() {
    this.props.fromApp();
    const productSelected = this.props.user.products.filter(
      product => product._id == this.props.productId
    )[0];
    this.setState({
      ...this.state,
      product: productSelected
    });
  }

