import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import { NavLink } from "react-router-dom";

import 'date-fns';
import DateFnsUtils from '@date-io/date-fns';
import TextField from '@material-ui/core/TextField';
import {
  MuiPickersUtilsProvider,
  KeyboardTimePicker,
  KeyboardDatePicker,
} from '@material-ui/pickers';

export default class CreateBid extends Component {
  constructor(props) {
    super();
    this.state = {
      deadLine:"",
      from : "",
      to:"",
      location:""
    }
  }
  updateFormData(e,field){
    let newState = {
      ...this.state
    };
    newState[field] = e.target.value;

    this.setState(newState);
  }

  sendState(e) {
    console.log(e)
    e.preventDefault();
    this.props.fromApp(this.state);
    return <Redirect to='/' />
  }

  render() {
    return (
      <form>
        <input type="text" placeholder="FinalizaciÃ³in" name="name"  onChange={e => this.updateFormData(e, "name")} ></input>
        <input type="text" placeholder="Tagline" name="tagline"  onChange={e => this.updateFormData(e, "tagline")} ></input>
        <input type="text" placeholder="Created by" name="contributed_by"  onChange={e => this.updateFormData(e, "contributed_by")}></input>
        <button to="/" onClick={e => this.sendState(e)}>Submit</button>
        {/* <KeyboardTimePicker
          margin="normal"
          id="time-picker"
          label="Hora de recogida"
          value={this.state.selectedDate}
          onChange={this.state.handleDateChange}
          KeyboardButtonProps={{
            'aria-label': 'change time',
          }}
        /> */}
      </form>
    )
  }
}
