import React, { Component } from 'react'

export default class ShowProduct extends Component {
  constructor(props) {
    super(props)
    this.state= {
      myProduct: null,
      product: 4,
      prod: 0
    }
  }
  componentDidMount() {
    this.props.fromApp();
    if(this.props.user) {
      const productSelected= this.props.user.products.filter(product=> product._id==this.props.productId)[0]
      console.log("product selected", this.props.user.products[0]._id==this.props.productId)
      console.log("product selected", productSelected)
      this.setState({
        ...this.state,
        riki: 1,
        product: "1",
        myProduct: this.props.user
      })
      console.log(this.state)
    }


  }
  change() {
    this.setState({
      ...this.state,
      product:9,
      valueForB: undefined
    });
  }
  render() {

    return (
      <div>
        jnjkn
        <h1>{this.state.prod}</h1>
        <h2>sjn</h2>
        {/* {/* <h1>{this.state.product.description}</h1> */}
        {/* <h1>{this.state.myProduct}</h1> */}
        <button onClick={(e)=>this.change(e)}>change</button>
      </div>
    )
  }
}
