import AuthService from "./components/auth/Authservice";

export default class App extends Component {
  constructor(props) {
    super();
    this.state = {
      loggedInUser: null
    };
    this.service = new AuthService();

    this.fetchUser()
  }

  getUser = userObj => {
    this.setState({
      loggedInUser: userObj,
    });
  };

  logout = () => {
    this.service.logout()
    .then(() => {
      this.setState({
        loggedInUser
      })
    })
  }





  render() {
    return (
      <React.Fragment>
      <div className="App">
        <Login></Login>
        <Signup></Signup>
      </div>

    </React.Fragment>
    )
  }
}
