import React, { Component } from "react";


export default class ShowProduct extends Component {
  constructor(props) {
    super(props);
    this.state = {
      product: this.props.product,
      julito: 99
    };
  }
  componentDidMount(){

    this.props.productFromApp()
    this.setState({
      ...this.state,
      product: this.props.product,
      julito: 1
    })
  }
  change() {
    this.props.productFromApp()
    this.setState ({
      ...this.state,
      product: this.props.product,
      julito:55
    })
    console.log("%%%%", this.state)
  }

  render() {
    if(this.props.product) {
      return (
        <React.Fragment>
        <h1>Contenido de la caja</h1>
        <div>
          <img src={this.props.product.imgPath1} alt="product image"></img>
          <img src={this.props.product.imgPath2} alt="product image"></img>
          <img src={this.props.product.imgPath3} alt="product image"></img>
          <h1>{this.props.product.name}</h1>
          <p>{this.props.product.description}</p>
          <p>Precio: {this.props.product.price} â‚¬</p>
          <img src={this.props.product.owner.imgPath}></img>
          <p>{this.props.product.owner.username}</p>
          <button onClick={e => this.change(e)}>Comprar</button>
        </div>
        </React.Fragment>
      );
    }
    else {

      return (
        <React.Fragment>
          <h1>Loading...</h1>
          {/* <h2>{this.state.product._id}</h2> */}
          <h2>{this.state.julito}</h2>
          <button onClick={(e)=>this.change(e)}>Change</button>

        </React.Fragment>
      )
    }

  }
}
