import React, { Component } from "react";
import { withRouter } from 'react-router-dom';



class ShowMyPurchases extends Component {
  constructor(props) {
    super(props);
    this.state = {

    };
  }

  componentDidMount() {
    this.props.fromApp()
    .then(()=> {
      const productsOwned = this.props.products.filter(
        product => product.buyer == this.props.user._id
      );
      this.setState({
        ...this.state,
        products: productsOwned
      });
    })

  }

  render() {
    if(this.state.products && this.props.user) {
      return (
        <React.Fragment>
          <a className="back-btn"><i class="medium material-icons" onClick={this.goBack}>arrow_back</i></a>
        <h1>Mostrar caja</h1>
        <div>
          <img src={this.state.product.imgPath1} alt="product image"></img>
          <h1>{this.state.product.name}</h1>
          <p>{this.state.product.description}</p>
          <p>Precio: {this.state.product.price} â‚¬</p>
          <button onClick={e => this.change(e)}>Editar</button>
        </div>
        </React.Fragment>
      );

    }
    else {
      return (
        <PreloaderSpinner></PreloaderSpinner>

      )
    }

  }
}

export default withRouter(ShowMyPurchases)
