import React, { Component } from "react";
import { Redirect } from "react-router-dom";
import { NavLink } from "react-router-dom";

import RoutesService from "../../RoutesService";

export default class CreateProduct extends Component {
  constructor(props) {
    super();
    this.state = {
      file: null,
      imgPath1: "",
      imgPath2: "",
      imgPath3: "",

    };
    this.routes = new RoutesService();
  }
  updateFormData(e, field) {
    let newState = {
      ...this.state
    };
    newState[field] = e.target.value;

    this.setState(newState);
  }

  sendState(e) {
    e.preventDefault();
    console.log(this.state)
    this.routes.createProduct(this.state);
    //this.props.fromApp(this.state);
    // return <Redirect to='/' />
  }
  changeState(newValue) {
    this.setState({
      ...this.state,
      location: {
        lat: newValue.lat,
        lng: newValue.lng,
        address: newValue.address
      }
    });
  }

  handleChange(e) {
    console.log("archivos seleccionado")
    console.log(e.target.files[0])

    this.setState({
      ...this.state,
      file: e.target.files[0]
    })
  }

  handleSubmit(e, photo) {
    e.preventDefault()
    // Reuse of the method "addPicture" from the file '../api'
    this.routes.addPicture(this.state.file).then(photoData => {
      console.log("$$$$$",photoData)
      // let newPhotos = [...this.state.photos]
      // newPhotos.push(photoData)

      this.setState({
        ...this.state,
        [photo]: photoData
      })
      console.log(this.state)
    })
  }

  render() {
    return (
      <React.Fragment>
        <h1>Contenido de la caja</h1>
        <form>
          <label htmlFor="name">Nombre</label>
          <input
            type="text"
            placeholder="Contenido de la caja a la venta"
            name="name"
            id="input-name"
            onChange={e => this.updateFormData(e, "name")}
          ></input>
          <label htmlFor="description">Descripción</label>
          <input
            type="text"
            placeholder="Breve descripción"
            name="description"
            onChange={e => this.updateFormData(e, "description")}
          ></input>
          <label htmlFor="price">Precio</label>
          <input
            type="number"
            placeholder="Precio"
            name="price"
            onChange={e => this.updateFormData(e, "price")}
          ></input>



          <button onClick={e => this.sendState(e)}>Submit</button>
        </form>
        <div className="Home">
        <h2>Foto 1</h2>

        <form onSubmit={(e)=>this.handleSubmit(e, "imgPath1")}>
          <input type="file" onChange={(e)=>this.handleChange(e)} /> <br/>
          <button type="submit">Upload photo</button>
        </form>

        <img src={this.state.imgPath1} alt="product picture" />
      </div>
      </React.Fragment>
    );
  }
}
