import React, { Component } from "react";
import "./App.css";
import { Switch, Route, Redirect } from "react-router-dom";
import Login from "./components/auth/Login";
import Signup from "./components/auth/Signup";

import GoogleAuth from "./components/auth/GoogleAuth";
import AuthService from "./components/auth/Authservice";

import Home from "./components/pages/Home";
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
// import CreateBidForm from "./components/organisms/CreateBidForm";
import CreateBid from "./components/organisms/CreateBid";
import Create from "./components/organisms/Create";


export default class App extends Component {
  constructor(props) {
    super();
    this.state = {
      loggedInUser: null
    };
    this.service = new AuthService();


    this.fetchUser();
  }

  getUser = userObj => {
    this.setState({
      loggedInUser: userObj
    });
  };

  logout = () => {
    this.service.logout().then(() => {
      this.setState({
        loggedInUser: null
      });
    });
  };

  fetchUser = () => {
    return this.service
      .loggedin()
      .then(response => {
        this.setState({
          loggedInUser: response
        });
      })
      .catch(error => {
        this.setState({
          loggedInUser: false
        });
      });
  };

  render() {
    return (
      <div>
      <Switch>
      <Route
            exact
            path="/"
            render={() => {
              return (
              <React.Fragment>
              <Home></Home>
              <Home></Home>
              </React.Fragment>
              )
            }}
          />
      <Route
            exact
            path="/create-bid"
            render={() => {
              return (
              <React.Fragment>
                {/* <CreateBidForm></CreateBidForm> */}
                <CreateBid></CreateBid>
              </React.Fragment>
                );
            }}
          />
      <Route
            exact
            path="/create-product"
            render={() => {
              return ( 
              <React.Fragment>
                <CreateProduct></CreateProduct>

            </React.Fragment>)

            }}
          />
      <Route
            exact
            path="/login"
            render={() => {
              if(this.state.loggedInUser) {
                  return (
                    <React.Fragment>
                      <CssBaseline />
                    <Redirect to="/home"/>
                    <h1>Wellcome home</h1>
                    <button onClick={this.logout}>Logout</button>
                  </React.Fragment>
                  )
                }
                else {
                  return (
                    <React.Fragment>
                      <CssBaseline />
                    <div className="App">
                      <Login getUser={this.getUser}></Login>
                      <Signup getUser={this.getUser}></Signup>
                      <GoogleAuth getUser={this.getUser}></GoogleAuth>
                      <a href="http://localhost:3010/auth/google">Sign In with Google</a>
                      <Button variant="contained" color="primary">
                  Hello World
                </Button>

                    </div>
                  </React.Fragment>
                    )

                }
            }}
          />

      </Switch>
      </div>
    );

    // if(this.state.loggedInUser) {
    //   return (
    //     <React.Fragment>
    //       <CssBaseline />
    //     <Redirect to="/home"/>
    //     <h1>Wellcome home</h1>
    //     <button onClick={this.logout}>Logout</button>
    //   </React.Fragment>
    //   )
    // }
    // else {
    //   return (
    //     <React.Fragment>
    //       <CssBaseline />
