import React, { Component } from "react";
import "./App.css";
import { Switch, Route, Redirect } from "react-router-dom";
import Login from "./components/auth/Login";
import Signup from "./components/auth/Signup";
import AuthService from "./components/auth/Authservice";

export default class App extends Component {
  constructor(props) {
    super();
    this.state = {
      loggedInUser: null
    };
    this.service = new AuthService();

    this.fetchUser()
  }

  getUser = userObj => {
    this.setState({
      loggedInUser: userObj,
    });
  };

  logout = () => {
    this.service.logout()
    .then(() => {
      this.setState({
        loggedInUser: null
      });
    });
  };

  fetchUser = () => {
    return this.service.loggedin()
    .then(response => {
      this.setState({
        loggedInUser: response,
      });
    })
    .catch(error => {
      this.setState({
        loggedInUser: false
      });
    });
  }





  render() {
    if(this.state.loggedInUser) {
      <React.Fragment>
        <Redirect to="/"/>
        <h1>Wellcome home</h1>


      </React.Fragment>

    }
    else {


      <React.Fragment>
      <div className="App">
        <Login userVerified={this.getUser}></Login>
        <Signup sendLogin={this.getUser}></Signup>
      </div>
    </React.Fragment>






    }

  }
}
