import React, { Component } from 'react'

export default class login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username : "",
      password : ""
    }
  }
  changeState(e) {
    console.log(e.target);

    //const {name, value} = e.target;
    this.setState({
      ...this.state
    });
  }

  render() {
    return (
      <div>
        <h1>Login</h1>
        <form onSubmit={this.sendLogin}>
        <input placeholder="User name"  onChange={e=>this.changeState(e)} type="text"></input>
        <input placeholder="Password"  onChange={e=>this.changeState(e)} type="password"></input>
        <input type="submit" value="Login" />
        </form>
      </div>
    )
  }
}
