import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import { NavLink } from "react-router-dom";

import 'date-fns';
import DateFnsUtils from '@date-io/date-fns';
import TextField from '@material-ui/core/TextField';
import {
  MuiPickersUtilsProvider,
  KeyboardTimePicker,
  KeyboardDatePicker,
} from '@material-ui/pickers';
import RoutesService from "../../RoutesService";
import LocationSearchInput from "../atoms/LocationSearchInput"




export default class CreateBid extends Component {
  constructor(props) {
    super();
    this.state = {
      sent: false

    }
    this.routes = new RoutesService()
  }
  updateFormData(e,field){
    let newState = {
      ...this.state
    };
    newState[field] = e.target.value;

    this.setState(newState);
  }

  sendState(e) {
    e.preventDefault();
    this.routes.createBid(this.state);
    this.setState({
      ...this.state,
      sent: true
    })
    //this.props.fromApp(this.state);
    // return <Redirect to='/' />
  }
  changeState(newValue) {
    this.setState({
      ...this.state,
      location: {
        lat: newValue.lat,
        lng: newValue.lng,
        address: newValue.address
      }
    });
  }


  render() {
    if(this.state.sent) {

    }

  }
}
