import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import { NavLink } from "react-router-dom";

import RoutesService from "../../RoutesService";

export default class CreateProduct extends Component {
  constructor(props) {
    super();
    this.state = {

    }
    this.routes = new RoutesService()
  }
  updateFormData(e,field){
    let newState = {
      ...this.state
    };
    newState[field] = e.target.value;

    this.setState(newState);
  }

  sendState(e) {
    e.preventDefault();
    this.routes.createBid(this.state)
    //this.props.fromApp(this.state);
    // return <Redirect to='/' />
  }
  changeState(newValue) {

    this.setState({
      ...this.state,
      location: {
        lat: newValue.lat,
        lng: newValue.lng,
        address: newValue.address
      }
    });

  }


  render() {
    return (
      <React.Fragment>
  <h1>Contenido de la caja</h1>
      <form>
        <label  htmlFor="name">Nombre</label>
        <input type="text" placeholder="Contenido de la caja a la venta" name="name" id="input-name" onChange={e => this.updateFormData(e, "name")} ></input>
        <label htmlFor="description">Descripción</label>
        <input type="text" placeholder="Breve descripción" name="description"  onChange={e => this.updateFormData(e, "description")} ></input>
        <label htmlFor="price">Precio</label>
        <input type="number" placeholder="Precio" name="price"  onChange={e => this.updateFormData(e, "price")}></input>

        <button  onClick={e => this.sendState(e)}>Submit</button>

      </form>
      </React.Fragment>

    )
  }
}
