import React, { Component } from "react";
import { NavLink, withRouter } from "react-router-dom";
import { Tabs, Tab, Col, Row, Preloader } from "react-materialize";
import AllProducts from "../organisms/AllProducts";
import SearchBar from "../atoms/SearchBar";
import Bidmapcontainer from "../molecules/Bidmapcontainer";
import RoutesService from "../../RoutesService";
import ModalLogin from "../auth/ModalLogin";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      bids: null,
      products: null
    };
    this.router= new RoutesService;
  }

  componentDidMount() {
    this.router.getProducts()
    .then(response=> {
      let bids= response.map(product=> product.bid);
      bids.forEach(bid=> bid.productsList.map(id=> response.filter(product=> product._id===id)))
      let bidsId= []
      bids= bids.filter(bid=> {
        if(bidsId.includes(bid._id)) {
          return false
        }
        else {
          bidsId.push(bid._id);
          return true
        }
      })
      this.setState({
        ...this.state,
        products: response,
        bids: bids
      })
      console.log(this.state, this.props)
    })
  }


  render() {

    return (

      <div>
        <h1>
          Si te acabas de mudar o estás a punto de mudarte, podemos ayudarte
        </h1>
        {/* <NavLink to={"/create-bid"}>Me voy a mudar</NavLink>
        <NavLink to={"/create-bid"}>Me acabo de mudar</NavLink> */}
        <Tabs className="tab-demo z-depth-1 tabs-fixed-width">
          <Tab title="Me voy a mudar">
            <h2>¿Tienes demasiadas cosas y no vas a llevarlas a tu nuevo hogar?</h2>
            <h2>Estás de enhorabuena.</h2>
            <p>Tenemos el escaparate perfecto para venderlo todo el día exacto que tú decidas.</p>

            <div class="timeline">
  <div class="container left">
    <div class="content">
      <h2>Git 1.0</h2>
      <p>Visit this site to learn about Git and become comfortable with basic Git practices</p>
    </div>
  </div>
  <div class="container right">
    <div class="content">
      <h2>PLB</h2>
      <p>This self-paced course will teach you the basics of understanding code in less than one day</p>
    </div>
  </div>
    <div class="container left">
    <div class="content">
      <h2>PLVM</h2>
      <p>Now you are ready to download and complete the learning VM</p>
    </div>
  </div>
    <div class="container right">
    <div class="content">
      <h2>GSWP</h2>
      <p>At this point you are ready to take the in-person training to get some hands-on practice with an instructor</p>
    </div>
  </div>
  <div class="container left">
    <div class="content">
      <h2>P Arch</h2>
      <p>Ready for a deeper dive? This course will take you even further</p>
    </div>
  </div>
    <div class="container right">
    <div class="content">
      <h2>Keep going!</h2>
      <p>The journey never ends. Here are some resources to keep you on your learning path.</p>
    </div>
  </div>
</div>




          </Tab>
          <Tab title="Me acabo de mudar" active>
            {this.state.products && this.state.bids && this.props.centerMap ? (
              <div>

                {/* <SearchBar
                  updateSearch={searchText => this.makeSearch(searchText)}
                  // updateInStock={stockCheckbox => this.changeStock(stockCheckbox)}
                /> */}
                <Bidmapcontainer
                  bids={this.state.bids}
                  centerMap={this.props.centerMap}
                ></Bidmapcontainer>
                <AllProducts products={this.props.products}></AllProducts>
              </div>
            ) : (
              <div>
                    <Preloader flashing size="small" />
              <ModalLogin></ModalLogin>

              </div>


            )}
          </Tab>
        </Tabs>
      </div>
    );
  }
}

export default withRouter(Home);
