import React, { Component } from 'react'

export default class ShowProduct extends Component {
  constructor(props) {
    super(props)
    this.state= {
      isMyProduct: {},
      product: {},

    }
  }
  //por que mierda no sube las props al state
  componentDidMount() {
    this.props.fromApp();
    if(this.props.user) {
      const productSelected= this.props.user.products.filter(product=> product._id==this.props.productId)[0];
      this.setState({
        ...this.state,
        product: productSelected,
        isMyProduct: this.props.user
      })
    }
  }

  render() {

    return (
      <div>
        jnjkn
        <img src={this.state.product.imgPath1} alt="product image"></img>
        <img src={this.state.product.imgPath2} alt="product image"></img>
        <img src={this.state.product.imgPath3} alt="product image"></img>
        <h1>{this.state.product.name}</h1>
        <p>{this.state.product.description}</p>
        <h1>Price: {this.state.product.price} â‚¬</h1>
        <h1>{this.state.isMyProduct.username}</h1>
        <button onClick={(e)=>this.change(e)}>change</button>
      </div>
    )
  }
}
