import React, { Component } from "react";
import { Collapsible, CollapsibleItem, Icon } from 'react-materialize';

export default class CollapsibleProducs extends Component {
  render() {
    return (
      <div className="collapsible-container">
        <Collapsible accordion={false}>


        {this.props.products.map((product, idx) => {
          if(product.owner==this.props.user._id){
            return (
              <CollapsibleItem
              header={product.name}
              icon={product.active? 
                <div>
                  <p>Disponible</p>
                </div>


              : 
              <div>
              <i class="medium material-icons">done</i>
              <p>Vendido</p>
                            </div>
            }
              >
                <div className="collaps-open">
                <img src={product.imgPath1} alt="Product image"></img>
                <p>{product.description}</p>
                <p>{product.price} €</p>

                </div>


            </CollapsibleItem>
            );


          }
          else {
            return (
              <CollapsibleItem
              header={product.name}
              icon={product.active? 
                <div>
                  <p>Disponible</p>
                </div>


              : 
              <div>
              <i class="medium material-icons">done</i>
              <p>Vendido</p>
                            </div>
            }
              >
                <div className="collaps-open">
                <img src={product.imgPath1} alt="Product image"></img>
                <p>{product.description}</p>
                <p>{product.price} €</p>

                </div>


            </CollapsibleItem>
            );
          }

        })}



        </Collapsible>
      </div>
    );
  }
}
